function getCookie(e){const t=e+"=",s=decodeURIComponent(document.cookie),n=s.split(";");for(let s=0;s<n.length;s++){let e=n[s];for(;e.charAt(0)===" ";)e=e.substring(1);if(e.indexOf(t)===0)return e.substring(t.length,e.length)}return""}function setCookie(e,t,n){const s=new Date;s.setTime(s.getTime()+n*24*60*60*1e3);const o="expires="+s.toUTCString();document.cookie=e+"="+t+";"+o+";path=/"}const cookieBanner=document.querySelector("#cookie-banner"),hasCookieConsent=getCookie("cookies-consent");hasCookieConsent||cookieBanner.classList.remove("hidden");const consentCta=cookieBanner.querySelector("#consent-cookies");consentCta.addEventListener("click",()=>{cookieBanner.classList.add("hidden"),setCookie("cookies-consent",1,365)})